mapper.select:
select p.pjt_id, p.pjt_nm, p.pjt_dtl, date_format(p.pjt_st_dt, "%Y-%m-%d") as pjt_st_dt, date_format(p.pjt_end_dt, "%Y-%m-%d") as pjt_end_dt, p.req_user_id, p.ins_user_id, p.status, p.server_id, p.created_yn, s.server_model, s.server_type, u1.user_group, u2.user_nm from project p left join server s on p.server_id = s.server_id join user u1 on p.req_user_id = u1.user_id join user u2 on p.ins_user_id = u2.user_id
s.server_type = #{server_type}
and p.pjt_end_dt <= #{pjt_end_dt}
and u2.user_nm = #{ins_user_id}
and p.pjt_nm like concat("%", #{pjt_nm}, "%")
limit #{pageStart} , #{amount}

select p.pjt_id, p.pjt_nm, p.pjt_dtl, date_format(p.pjt_st_dt, "%Y-%m-%d") as pjt_st_dt, date_format(p.pjt_end_dt, "%Y-%m-%d") as pjt_end_dt, p.req_user_id, p.ins_user_id, p.status, p.server_id, p.created_yn, s.server_model, u1.user_group, u2.user_nm
from project p left join server s on p.server_id = s.server_id join user u1 on p.req_user_id = u1.user_id join user u2 on p.ins_user_id = u2.user_id
where pjt_id in (select pjt_id from pjt_mem where user_id = #{user_id}) order by pjt_end_dt desc limit #{cri.pageStart}, #{cri.amount}

select * from (select p.pjt_id, p.pjt_nm, p.pjt_dtl, date_format(p.pjt_st_dt, "%Y-%m-%d") as pjt_st_dt, date_format(p.pjt_end_dt, "%Y-%m-%d") as pjt_end_dt, p.req_user_id, p.ins_user_id, p.status, p.server_id, p.created_yn, s.server_model, s.server_type, u1.user_nm, u2.user_group from project p join server s on p.server_id = s.server_id join user u1 on p.ins_user_id = u1.user_id join user u2 on p.req_user_id = u2.user_id) as ps
server_type = #{server_type}
and date_format(pjt_end_dt, "%Y-%m-%d") <= #{pjt_end_dt}
and user_nm = #{ins_user_id}

select count(*) from (select p.pjt_id, p.pjt_nm, p.pjt_dtl, date_format(p.pjt_st_dt, "%Y-%m-%d") as pjt_st_dt, date_format(p.pjt_end_dt, "%Y-%m-%d") as pjt_end_dt, p.req_user_id, p.ins_user_id, p.status, p.server_id, p.created_yn, s.server_model, s.server_type, u1.user_nm, u2.user_group from project p join server s on p.server_id = s.server_id join user u1 on p.ins_user_id = u1.user_id join user u2 on p.req_user_id = u2.user_id
s.server_type = #{server_type}
and p.pjt_end_dt <= #{pjt_end_dt}
and u2.user_nm = #{ins_user_id}
and p.pjt_nm like concat("%", #{pjt_nm}, "%")
) as ps

select count(*) from (select p.pjt_id, p.pjt_nm, p.pjt_dtl, date_format(p.pjt_st_dt, "%Y-%m-%d") as pjt_st_dt, date_format(p.pjt_end_dt, "%Y-%m-%d") as pjt_end_dt, p.req_user_id, p.ins_user_id, p.status, p.server_id, p.created_yn, s.server_model, s.server_type, u1.user_nm, u2.user_group from project p join server s on p.server_id = s.server_id join user u1 on p.ins_user_id = u1.user_id join user u2 on p.req_user_id = u2.user_id) as ps
server_type = #{server_type}
and date_format(pjt_end_dt, "%Y-%m-%d") <= #{pjt_end_dt}
and user_nm = #{ins_user_id}
